client/src/utils/quizParser.ts.content:
import Papa from 'papaparse';
import { Question } from './storage';

export const parseQuizFile = (file: File): Promise<Question[]> => {
  return new Promise((resolve, reject) => {
    const reader = new FileReader();
    reader.onload = (event) => {
      const textContent = event.target?.result as string;
      parseQuizText(textContent)
        .then(resolve)
        .catch(err => reject(new Error(`Error in ${file.name}: ${err.message}`)));
    };
    reader.onerror = () => reject(new Error('Could not read file.'));
    reader.readAsText(file);
  });
};

export const parseQuizText = (textContent: string): Promise<Question[]> => {
  return new Promise((resolve, reject) => {
    try {
      const text = textContent.trim();
      if (text.length === 0) {
        return reject(new Error("Pasted text is empty."));
      }

      // 1. Check for JSON
      if (text.startsWith('[') || text.startsWith('{')) {
        try {
          let data = JSON.parse(text);
          // If it's a single object, wrap it in an array
          if (!Array.isArray(data)) {
            data = [data];
          }
          // Validate the structure
          const validatedQuestions = data.map((item: any, index: number) => {
            if (!item.question) {
              throw new Error(`Question ${index + 1} is missing the "question" field`);
            }
            if (!item.options || typeof item.options !== 'object') {
              throw new Error(`Question ${index + 1} is missing valid "options" object`);
            }
            if (!item.answer) {
              throw new Error(`Question ${index + 1} is missing the "answer" field`);
            }
            return {
              id: item.id || `q${index + 1}`,
              question: item.question,
              options: item.options,
              answer: item.answer.toLowerCase(),
              explanation: item.explanation || undefined,
              category: item.category || undefined,
              level: item.level || undefined,
            };
          });
          return resolve(validatedQuestions);
        } catch (err) {
          return reject(new Error(`JSON Parse Error: ${err instanceof Error ? err.message : 'Invalid JSON'}`));
        }
      }

      // 2. Check for TXT format
      if (text.includes('---') || (text.includes('Q:') && text.includes('Answer:'))) {
        const questions: Question[] = [];
        const blocks = text.split('---');

        for (const block of blocks) {
          if (block.trim().length === 0) continue;

          const lines = block.trim().split('\n');
          const q: Partial<Question> = { 
            id: `q${questions.length + 1}`, 
            options: {} 
          };

          lines.forEach(line => {
            const colonIndex = line.indexOf(':');
            if (colonIndex === -1) return;

            const key = line.substring(0, colonIndex).trim();
            const value = line.substring(colonIndex + 1).trim();

            if (key === 'Q' || key === 'Question') q.question = value;
            if (key === 'A') q.options!.a = value;
            if (key === 'B') q.options!.b = value;
            if (key === 'C') q.options!.c = value;
            if (key === 'D') q.options!.d = value;
            if (key === 'Answer') q.answer = value.toLowerCase();
            if (key === 'Explanation') q.explanation = value;
            if (key === 'Category') q.category = value;
            if (key === 'Level') q.level = value;
          });

          if (!q.question || !q.answer) {
            return reject(new Error('Invalid TXT format. Each question must have "Q:" and "Answer:".'));
          }

          questions.push(q as Question);
        }

        return resolve(questions);
      }

      // 3. Check for CSV
      if (text.includes(',')) {
        const result = Papa.parse(text, { header: true, skipEmptyLines: true });

        if (result.errors.length) {
          return reject(new Error(`CSV Parse Error: ${result.errors[0].message}`));
        }

        const questions: Question[] = result.data.map((row: any, i: number) => {
          if (!row.question || !row.answer) {
            throw new Error(`CSV Error: Row ${i + 2} is missing required columns (question, answer).`);
          }

          const options: { [key: string]: string } = {};
          if (row.option_a) options.a = row.option_a;
          if (row.option_b) options.b = row.option_b;
          if (row.option_c) options.c = row.option_c;
          if (row.option_d) options.d = row.option_d;

          if (Object.keys(options).length === 0) {
            throw new Error(`CSV Error: Row ${i + 2} has no options (need at least option_a).`);
          }

          return {
            id: row.id || `q${i + 1}`,
            question: row.question,
            options: options,
            answer: row.answer.toLowerCase(),
            explanation: row.explanation || undefined,
            category: row.category || undefined,
            level: row.level || undefined,
          };
        });

        return resolve(questions);
      }

      // 4. No match
      reject(new Error("Could not detect format. Please paste valid JSON, CSV, or our Simple TXT format."));

    } catch (err) {
      reject(new Error(`Parsing failed: ${err instanceof Error ? err.message : 'Unknown error'}`));
    }
  });
};

client/src/components/ui/form.tsx.content:
"use client"

import * as React from "react"
import * as LabelPrimitive from "@radix-ui/react-label"
import { Slot } from "@radix-ui/react-slot"
import {
  Controller,
  FormProvider,
  useFormContext,
  type ControllerProps,
  type FieldPath,
  type FieldValues,
} from "react-hook-form"

import { cn } from "@/lib/utils"
import { Label } from "@/components/ui/label"

const Form = FormProvider

type FormFieldContextValue<
  TFieldValues extends FieldValues = FieldValues,
  TName extends FieldPath<TFieldValues> = FieldPath<TFieldValues>
> = {
  name: TName
}

const FormFieldContext = React.createContext<FormFieldContextValue>(
  {} as FormFieldContextValue
)

const FormField = <
  TFieldValues extends FieldValues = FieldValues,
  TName extends FieldPath<TFieldValues> = FieldPath<TFieldValues>
>({
  ...props
}: ControllerProps<TFieldValues, TName>) => {
  return (
    <FormFieldContext.Provider value={{ name: props.name }}>
      <Controller {...props} />
    </FormFieldContext.Provider>
  )
}

const useFormField = () => {
  const fieldContext = React.useContext(FormFieldContext)
  const itemContext = React.useContext(FormItemContext)
  const { getFieldState, formState } = useFormContext()

  const fieldState = getFieldState(fieldContext.name, formState)

  if (!fieldContext) {
    throw new Error("useFormField should be used within <FormField>")
  }

  const { id } = itemContext

  return {
    id,
    name: fieldContext.name,
    formItemId: `${id}-form-item`,
    formDescriptionId: `${id}-form-item-description`,
    formMessageId: `${id}-form-item-message`,
    ...fieldState,
  }
}

type FormItemContextValue = {
  id: string
}

const FormItemContext = React.createContext<FormItemContextValue>(
  {} as FormItemContextValue
)

const FormItem = React.forwardRef<
  HTMLDivElement,
  React.HTMLAttributes<HTMLDivElement>
>(({ className, ...props }, ref) => {
  const id = React.useId()

  return (
    <FormItemContext.Provider value={{ id }}>
      <div ref={ref} className={cn("space-y-2", className)} {...props} />
    </FormItemContext.Provider>
  )
})
FormItem.displayName = "FormItem"

const FormLabel = React.forwardRef<
  React.ElementRef<typeof LabelPrimitive.Root>,
  React.ComponentPropsWithoutRef<typeof LabelPrimitive.Root>
>(({ className, ...props }, ref) => {
  const { error, formItemId } = useFormField()

  return (
    <Label
      ref={ref}
      className={cn(error && "text-destructive", className)}
      htmlFor={formItemId}
      {...props}
    />
  )
})
FormLabel.displayName = "FormLabel"

const FormControl = React.forwardRef<
  React.ElementRef<typeof Slot>,
  React.ComponentPropsWithoutRef<typeof Slot>
>(({ ...props }, ref) => {
  const { error, formItemId, formDescriptionId, formMessageId } = useFormField()

  return (
    <Slot
      ref={ref}
      id={formItemId}
      aria-describedby={
        !error
          ? `${formDescriptionId}`
          : `${formDescriptionId} ${formMessageId}`
      }
      aria-invalid={!!error}
      {...props}
    />
  )
})
FormControl.displayName = "FormControl"

const FormDescription = React.forwardRef<
  HTMLParagraphElement,
  React.HTMLAttributes<HTMLParagraphElement>
>(({ className, ...props }, ref) => {
  const { formDescriptionId } = useFormField()

  return (
    <p
      ref={ref}
      id={formDescriptionId}
      className={cn("text-sm text-muted-foreground", className)}
      {...props}
    />
  )
})
FormDescription.displayName = "FormDescription"

const FormMessage = React.forwardRef<
  HTMLParagraphElement,
  React.HTMLAttributes<HTMLParagraphElement>
>(({ className, children, ...props }, ref) => {
  const { error, formMessageId } = useFormField()
  const body = error ? String(error?.message ?? "") : children

  if (!body) {
    return null
  }

  return (
    <p
      ref={ref}
      id={formMessageId}
      className={cn("text-sm font-medium text-destructive", className)}
      {...props}
    >
      {body}
    </p>
  )
})
FormMessage.displayName = "FormMessage"

export {
  useFormField,
  Form,
  FormItem,
  FormLabel,
  FormControl,
  FormDescription,
  FormMessage,
  FormField,
}

client/src/pages/Settings.tsx.content:
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from "@/components/ui/card";
import { useToast } from "@/hooks/use-toast";
import { Download, Trash2, ArrowLeft } from "lucide-react";
import { Link, useLocation } from "wouter";
import { exportData, clearAllData } from "@/utils/storage";
import {
  AlertDialog,
  AlertDialogAction,
  AlertDialogCancel,
  AlertDialogContent,
  AlertDialogDescription,
  AlertDialogFooter,
  AlertDialogHeader,
  AlertDialogTitle,
  AlertDialogTrigger,
} from "@/components/ui/alert-dialog";

export default function Settings() {
  const { toast } = useToast();
  const [, setLocation] = useLocation();

  const handleExport = () => {
    try {
      exportData();
      toast({
        title: "Data exported",
        description: "Your backup file has been downloaded",
      });
    } catch (error) {
      toast({
        title: "Export failed",
        description: error instanceof Error ? error.message : "Unknown error",
        variant: "destructive",
      });
    }
  };

  const handleClearData = () => {
    try {
      clearAllData();
      toast({
        title: "Data cleared",
        description: "All quizzes and results have been deleted",
        variant: "destructive",
      });
      setTimeout(() => setLocation("/"), 1000);
    } catch (error) {
      toast({
        title: "Clear failed",
        description: error instanceof Error ? error.message : "Unknown error",
        variant: "destructive",
      });
    }
  };

  return (
    <div className="container mx-auto px-4 py-8 max-w-3xl">
      <div className="mb-6">
        <Button variant="ghost" asChild data-testid="button-back">
          <Link href="/">
            <ArrowLeft className="h-4 w-4 mr-2" />
            Back to Dashboard
          </Link>
        </Button>
      </div>

      <div className="mb-8">
        <h1 className="font-heading text-4xl font-bold text-foreground mb-2">
          Settings
        </h1>
        <p className="text-muted-foreground">
          Manage your data and preferences
        </p>
      </div>

      <div className="space-y-6">
        <Card>
          <CardHeader>
            <CardTitle className="font-heading">Data Backup</CardTitle>
            <CardDescription>
              Export all your quizzes and results as a JSON file
            </CardDescription>
          </CardHeader>
          <CardContent>
            <Button onClick={handleExport} variant="outline" data-testid="button-export">
              <Download className="h-4 w-4 mr-2" />
              Export Data
            </Button>
            <p className="text-xs text-muted-foreground mt-3">
              Your data is stored locally in your browser. Export regularly to keep a backup.
            </p>
          </CardContent>
        </Card>

        <Card className="border-destructive/30">
          <CardHeader>
            <CardTitle className="font-heading text-destructive">Danger Zone</CardTitle>
            <CardDescription>
              Irreversible actions that will permanently delete your data
            </CardDescription>
          </CardHeader>
          <CardContent>
            <AlertDialog>
              <AlertDialogTrigger asChild>
                <Button variant="destructive" data-testid="button-clear-trigger">
                  <Trash2 className="h-4 w-4 mr-2" />
                  Clear All Data
                </Button>
              </AlertDialogTrigger>
              <AlertDialogContent>
                <AlertDialogHeader>
                  <AlertDialogTitle className="font-heading">Are you absolutely sure?</AlertDialogTitle>
                  <AlertDialogDescription>
                    This action cannot be undone. This will permanently delete all your quizzes,
                    results, and progress from your browser's storage.
                  </AlertDialogDescription>
                </AlertDialogHeader>
                <AlertDialogFooter>
                  <AlertDialogCancel data-testid="button-cancel">Cancel</AlertDialogCancel>
                  <AlertDialogAction
                    onClick={handleClearData}
                    className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
                    data-testid="button-clear-confirm"
                  >
                    Yes, delete everything
                  </AlertDialogAction>
                </AlertDialogFooter>
              </AlertDialogContent>
            </AlertDialog>
          </CardContent>
        </Card>

        <Card>
          <CardHeader>
            <CardTitle className="font-heading">About</CardTitle>
            <CardDescription>
              MockQuiz is a privacy-first quiz application
            </CardDescription>
          </CardHeader>
          <CardContent>
            <div className="text-sm text-muted-foreground space-y-2">
              <p>
                All your data is stored locally in your browser using localStorage.
                No servers, no tracking, 100% private.
              </p>
              <p>
                Works completely offline once loaded. Create quizzes using AI-generated
                content or upload your own questions.
              </p>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

